package org.usfirst.frc862.valkyrie.commands;
import org.usfirst.frc862.util.Logger;
import org.usfirst.frc862.util.StatefulCommand;
import org.usfirst.frc862.valkyrie.Robot;

import com.team254.lib.util.ChezyMath;

/**
 *
 */
public class SpinLeft extends StatefulCommand {
    enum States {
        ROLL_BACK_RIGHT, ROLL_FORWARD_LEFT, DONE
    }

    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=VARIABLE_DECLARATIONS
 
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=VARIABLE_DECLARATIONS

    private double start_angle;

    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTOR
    public SpinLeft() {

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTOR
        super(States.ROLL_BACK_RIGHT);
        
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=VARIABLE_SETTING

        // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=VARIABLE_SETTING
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
        requires(Robot.driveTrain);

        // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
        requires(Robot.shifter);
    }

    // Called just before this Command runs the first time
    protected void initialize() {
        start_angle = Robot.driveTrain.getGyroAngle();
        // Robot.shifter.upShift();
        setState(States.ROLL_BACK_RIGHT);
    }

    public void rollBackRight() {
        Robot.driveTrain.teleop(-0.2, 1.0);
        Logger.debug("rollbackRight: " + error());
        if (error() < -170 || error() > 90) {
            this.setState(States.ROLL_FORWARD_LEFT);
        }
    }
    
    public void rollForwardLeft() {
        Robot.driveTrain.teleop(-1.0, 0.2);
        Logger.debug("rollForwardLeft: " + error());
        if (Math.abs(error()) < 20) {
            this.setState(States.DONE);            
        }
    }
    
    public void done() {
        Robot.driveTrain.teleop(1, 1);
    }
    
    public double error() {
        double heading = Robot.driveTrain.getGyroAngle();
        return ChezyMath.boundAngleNeg180to180Degrees(start_angle - heading);        
    }
    
    // Make this return true when this Command no longer needs to run execute()
    protected boolean isFinished() {
        return false;
    }

    // Called once after isFinished returns true
    protected void end() {
        Robot.driveTrain.teleop(0, 0);
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    protected void interrupted() {
        end();
    }
}
